import { CartState } from "../context/Context";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import '../assets/cart.css'

const Cart = () => {
  const { updateCart, cart } = CartState();

  return (
    <>
    <h1>My Cart</h1>
    <Container fluid className="container-products-cart">
      {cart
        ? cart.map((product) => (
            <Row>
              <Col className="col">

                  {product.name}
              </Col>
              <Col className="col">
                  <button
                    onClick={() => {
                      updateCart(product.id, "incr");
                    }}
                  >
                    +
                  </button>
                  <button
                    onClick={() => {
                      updateCart(product.id, "decr");
                    }}
                  >
                    -
                  </button>
                  <div>Quantit√†: {product.quantity}</div>

                </Col>
            </Row>
          ))
        : null}
        <Row>
          <Col className="col"/>
        </Row>
        <Row>
          <Col className="col">
          <Button>Clear my Cart</Button>
          <Button>Checkout</Button>
          </Col>
        </Row>
    </Container>
    </>
  );
};

export default Cart;
