import {createContext, useContext, useState} from 'react'
import axios from "axios"
import api from '../api/api'
import {useEffect} from 'react'

const Cart=createContext();

const Context=({children})=>{
    const [cart,setCart]=useState([])




    const addCart =async (product)=>{
        // const isExist=cart.filter(elem=>elem.id===product.id)
        // if(isExist)
        // {
        //   return 
        // }
        await axios.post(`http://localhost:8080/addToCart/${product.id}`)
        setCart(cart.concat(product))
      }
      useEffect(async() => {
        const resCart=await api.getCartEndPoint()
        setCart(cart.concat(resCart.data.products))
        console.log("Sono resCart",resCart.data.products)
    },[addCart])

      const getCart= async ()=>{
        const resCart=await api.getCartEndPoint()
        setCart([...cart],resCart.data.products)
        return cart
      }

    return (
        <Cart.Provider value={{cart,getCart,addCart}}>
            {children}
        </Cart.Provider>
    )
}

export const CartState=()=>{
    return useContext(Cart);
}

export default Context;
