import { CartState } from "../context/Context";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import "../assets/cart.css";


const Cart = () => {
  const { updateCart, cart,clearCart,checkout } = CartState();

  const total=cart.reduce((prev,current)=>{
    return prev + (current.quantity*current.price)
  },0)

  const getAmount = (productAmount,productQuantity) => {
    const singleAmount=productAmount*productQuantity
    return <span>Amount: {singleAmount}$</span>
  }



  return (
    <>
      <h1>My Cart</h1>
      <Container fluid className="container-products-cart">
        {cart.length
          ? cart.map((product) => (
              <Row>
                <Col className="col-cart justify-content-end" sm>
                  {" "}
                  <img
                    style={{ width: "100px", height: "90px" }}
                    src="https://www.flmedical.com/wp-content/uploads/2016/12/img-vuota-300x221.jpg"
                    onClick={() => {
                      console.log("vado a pagina prodotto");
                    }}
                  />
                </Col>
                <Col className=" col-cart justify-content-center" sm>
                  {" "}
                  <h4>{product.name}</h4>
                </Col>

                <Col className="col-cart justify-content-start" sm>
                  <Button
                    variant="outline-dark"
                    onClick={() => {
                      updateCart(product.id, "decr");
                    }}
                  >
                    -
                  </Button>
                  <span>Quantit√†: {product.quantity}</span>
                  <Button
                    variant="outline-dark"
                    onClick={() => {
                      updateCart(product.id, "incr");
                    }}
                  >
                    +
                  </Button>
                </Col>
                <Col className="col-cart justify-content-start" sm>
                  {getAmount(product.price,product.quantity)}
                </Col>
                <Col className="col-cart justify-content-start"  sm>
                    <Button
                    variant="outline-dark"
                      onClick={() => {updateCart(product.id, "clearId")}}
                    >
                      x
                    </Button>
                </Col>
              </Row>
            ))
          : null}

      </Container>
    </>
  );
};

export default Cart;
