import {createContext, useContext, useState} from 'react'
import axios from "axios"
import api from '../api/api'
import {useEffect} from 'react'

const Cart=createContext();

const Context=({children})=>{
    const [cart,setCart]=useState([])

    const addCart =async (product)=>{
        // const isExist=cart.filter(elem=>elem.id===product.id)
        // if(isExist)
        // {
        //   return 
        // }

        const res=await axios.post(`http://localhost:8080/addToCart/${product.id}`)
        if(res)
        {
          await getCart()
        }

        //cart.concat ti ritorna una copia 
      }


      useEffect(() => {
        (async()=>{
          const res=await api.getCartEndPoint()
          setCart((state)=>[...state, ...res.data.products])
        })()
    },[])



      const getCart= async ()=>{
        const resCart=await api.getCartEndPoint()
        setCart((state)=>[...state, ...resCart.data.products])
        return cart
      }

    return (
        <Cart.Provider value={{cart,getCart,addCart}}>
            {children}
        </Cart.Provider>
    )
}

export const CartState=()=>{
    const context=useContext(Cart);
    if(context===undefined)
    {
      throw Error("Context deve essere usato dentro Cart.Provider")
    }
    return context
}

export default Context;
