import { Navbar, Container, Nav } from "react-bootstrap";
import { CartState } from "../context/Context";
import { LoginState } from "../context/ContextLogin";


export default function NavBar() {

  const {
    user
  } = LoginState();

  const {cart}=CartState(0);

  const total=cart.reduce((prev,current)=>{
    return prev + (current.quantity)
  },0)

  return (
    <Navbar bg="light" expand="md">
      <Container>
        <Navbar.Brand href="/">Home</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link href="/products">Products</Nav.Link>
        </Nav>
        <Navbar.Toggle />
        <Navbar.Collapse className="justify-content-end">
          <Nav.Link href="/cart">Cart {total} (accesso solo con token)</Nav.Link>
          {user ? <Nav.Link href="/login">Logout</Nav.Link> : <Nav.Link href="/login">Login</Nav.Link>}

          {/* <Navbar.Text>
            Signed in as: <a href="#login">Mark Otto</a>
          </Navbar.Text> */}
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
