import {createContext, useContext, useState} from 'react'
import api from '../api/api'

const Cart=createContext();

const Context=({children})=>{
    const [cart,setCart]=useState([])

    const addCart = async(product)=>{
        const isExist=cart.filter(elem=>elem.id===product.id)
        if(isExist)
        {
          return 
        }
        console.log(await api.addToCart(product.id,product))
        const productWithQuantity={...product,quantity:1}
        setCart([...cart],productWithQuantity)

      }

      const getCart= async ()=>{
        const resCart=await api.getCartEndPoint()
        setCart([...cart],resCart.data.products)
        return cart
      }

    return (
        <Cart.Provider value={{cart,getCart,addCart}}>
            {children}
        </Cart.Provider>
    )
}

export const CartState=()=>{
    return useContext(Cart);
}

export default Context;
