import { CartState } from "../context/Context";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Button from "react-bootstrap/Button";
import "../assets/cart.css";

const Cart = () => {
  const { updateCart, cart } = CartState();

  return (
    <>
      <h1>My Cart</h1>
      <Container fluid className="container-products-cart">
        {cart
          ? cart.map((product) => (
              <Row>

                  <Col className="justify-content-end"> <img
                    style={{ width: "100px", height: "90px" }}
                    src="https://www.flmedical.com/wp-content/uploads/2016/12/img-vuota-300x221.jpg"
                    onClick={() => {
                      console.log("vado a pagina prodotto");
                    }}
                  />
                  </Col>
                  <Col className="justify-content-center"> <h4>{product.name}</h4></Col>

                <Col className="col justify-content-start" xs="4">
                  <button
                    onClick={() => {
                      updateCart(product.id, "decr");
                    }}
                  >
                    -
                  </button>
                  <span>Quantit√†: {product.quantity}</span>
                  <button
                    onClick={() => {
                      updateCart(product.id, "incr");
                    }}
                  >
                    +
                  </button>
                </Col>

              </Row>
            ))
          : null}
        <Row>
          <Col className="col" />
        </Row>
        <Row>
          <Col className="col justify-content-end">
            <Button style={{marginRight:'80px'}}>Clear my Cart</Button>
            <Button>Checkout</Button>
          </Col>
        </Row>
      </Container>
    </>
  );
};

export default Cart;
