import { CartState } from "../context/Context";

export default function Product({product}){
    const {addCart}=CartState()

    return <div>
        <img style={{width:'300px',height:'auto'}} src="https://www.flmedical.com/wp-content/uploads/2016/12/img-vuota-300x221.jpg"/>
        <h3>{product.name}</h3>
        <h4>{product.price}$</h4>
        <button onClick={()=>{addCart(product)}}/>
    </div>
}
