import { CartState } from "../context/Context";
import Button from "react-bootstrap/Button";
import { useState } from "react";
import Container from "react-bootstrap/Container";

export default function Counter({ quantityProduct, productId }) {
  const [counter, setCounter] = useState(quantityProduct);
  const { updateCart, updateQuantity } = CartState();

  const addCount = () => {
    setCounter(counter + 1);
  };

  const decrCount = () => {
    setCounter(counter - 1);
  };

  return (
    <Container className="btn-counter">
      <Row>
        <Col>
          <Button onClick={() => decrCount()}>-</Button>
          <span>Quantity: {counter}</span>
          <Button onClick={() => addCount()} style={{ marginRight: "10px" }}>
            +
          </Button>
        </Col>
        <Col>
          <Button onClick={() => updateQuantity(productId, counter)}>
            Confirm quantity
          </Button>
        </Col>
      </Row>
    </Container>
  );
}
