import { useParams } from "react-router-dom";
import ClientGetApi from "../api/ClientGetApi";
import { CircularProgress } from "@material-ui/core";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import { useEffect } from "react";

export default function SingleProduct() {
  const { id } = useParams();

  const clientProduct = ClientGetApi();

  useEffect(() => {
    clientProduct.fetchProduct(id);
    console.log("sono dentro single", clientProduct.data);
  }, []);

  if (clientProduct.loading) {
    return (
      <Container fluid>
        <Row>
          <Col>
            <CircularProgress />
            aaaa
          </Col>
        </Row>
      </Container>
    );
  }

  if (clientProduct.error) {
    return (
      <Container fluid>
        <Row>
          <Col>Error: {clientProduct.error.message}</Col>
        </Row>
      </Container>
    );
  }

  if (!clientProduct.data || clientProduct.data.length === 0) {
    return (
      <Container fluid>
        <Row>
          <Col>Non ci sono info</Col>
        </Row>
      </Container>
    );
  }

  return (
    <div>
      <Container fluid>
       {clientProduct.map((elem)=>console.log(elem))}
          <>
            <Row>
              <Col>
                <img
                  style={{ width: "300px", height: "auto" }}
                  src="https://www.flmedical.com/wp-content/uploads/2016/12/img-vuota-300x221.jpg"
                />
              </Col>
            </Row>
            <Row>
              <Col>
                <div>
                  <h4>{clientProduct.data.name}</h4>
                  <p>Avaliable: {clientProduct.available}</p>
                  <p>price: {clientProduct.price}$</p>
                </div>
              </Col>
            </Row>
            <Row>
              <Col></Col>
            </Row>
          </>

      </Container>
    </div>
  );
}
