import { CartState } from "../context/Context";
import Button from "react-bootstrap/Button";
import {useState}from 'react'

export default function Counter({quantityProduct}){
    const [counter,setCounter]=useState(quantityProduct)
    const { updateCart } = CartState();

    const addCount=()=>{
        setCounter(counter+1)
    }

    const decrCount=()=>{
        setCounter(counter-1)
    }

    return <div className="btn-counter">
        <Button onClick={()=>decrCount()}>-</Button>
        <span>Quantity: {counter}</span>
        <Button onClick={()=>addCount()}>+</Button>
        <Button>Confirm quantity</Button>
    </div>
}
