import { createContext, useContext, useState } from "react";
import api from "../api/api";
import { useEffect } from "react";

const Login = createContext();

const ContextLogin = ({ children }) => {
    const[username,setUsername] =useState("")
    const[password,setPassword] = useState("")
    const[error,setError]=useState(null)
    const [loading,setLoading]=useState(false)


    const login=async (email,passord)=>{
        try{
            await api.loginEndPoint(email,passord)
        }
        catch(error)
        {
            if(error.response){
                console.warn(error.response.data)
            }
            else console.warn(error)
        }
    }

  return (
    <Login.Provider 
    value={{username,
            setUsername,
            setPassword,
            password,
            setError,
            error,
            setLoading,
            loading,
            login}}>
      {children}
    </Login.Provider>
  );
};

export const LoginState = () => {
  const context = useContext(Login);
  if (context === undefined) {
    throw Error("Context deve essere usato dentro Cart.Provider");
  }
  return context;
};

export default ContextLogin;
